package meeting

class App {
    fun meeting(s: String): String {
        // your code
        var result = s.split(';').map({ colon_separated_fname_lname -> colon_separated_fname_lname.split(':').reversed().joinToString() }).sorted()
        return result.map { "(${it.uppercase()})" }.joinToString("")
    }
}

fun main(args Array<String>) {
    println(App().meeting(args[0]))
}
